package FirstHand_D_25;

public class count_the_number_digits {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int num = 586245;
      int count = 0;
      while(num!=0);
      num=num/10;
      {
    	  count++;
      }
      {
    	  System.out.println(count++);
      }
      
	}

}
